interface Submenupicker {
    (element: JQuery): Submenupicker;
    init: () => void;
    click: (event: MouseEvent) => void;
    toggle: () => void;
    hide: (event: Event) => void;
    close: () => void;
    keydown: (event: KeyboardEvent) => void;
    item_keydown: (event: KeyboardEvent) => void;
}


interface JQuery {
    submenupicker(elements?: any): JQuery
    data(key: 'bs.submenu'): Submenupicker;
}