/// <reference types="knockout" />
/// <reference types="knockout.postbox" />
declare abstract class AggregatedComponents {
    constructor();
    dispose(): void;
    abstract getOptionalComponent(grid: any): any;
    abstract postAdd(gridElement: any): any;
    addGrid: (gridElement: string) => boolean;
    removeGrid: (gridElement: string) => void;
    setActiveGrid: (gridElement: string) => void;
    activeGrid: KnockoutObservable<string>;
    grids: KnockoutObservableArray<any>;
    components: IDictionary<OptionalComponent>;
    subscriptions: KnockoutSubscription[];
}
export = AggregatedComponents;
